mapper.update:
update checkout
set user_id = #{userId},
    end     = current_timestamp,
    remark  = #{remark},
    state   = #{state}
where checkout_id = #{checkoutId}

mapper.select:
select a.checkout_id,a.rent_id,a.user_id,a.start,IFNULL(a.end,"待处理")as
end,a.remark,a.state,a.message,c.house_name
from checkout as a,rent as b,house as c
b.rent_id = a.rent_id
    and b.house_id = c.house_id
    
        and c.house_name = #{houseName}
    
    
        and DATE_FORMAT(a.start, '%Y-%m-%d') = #{time}
    

${ew.customSqlSegment}
