select a.house_name, IFNULL(c.num_electric,"待处理")as num_electric, IFNULL(c.cost_electric,"待处理") as cost_electric , IFNULL(c.upload_electric ,"待处理") as upload_electric, IFNULL(c.num_water,"待处理")as num_water, IFNULL(c.cost_water,"待处理") as cost_water , IFNULL(c.upload_water ,"待处理") as upload_water, c.cost_id, c.date , c.state, c.rent_id from house as a, rent as b, cost as c where a.house_id = b.house_id and b.rent_id = c.rent_id
and DATE_FORMAT(c.date , '%Y-%m') = #{date}
and a.house_name = #{houseName}
