mapper.select:
select house_id,house_name,style_name,balcony,area,FORMAT(price*area,2)as price,price as price_area from
house,style where
house.style_id = style.style_id and house.state = "空闲"

    and floor_id = #{floor}


    and style_name = #{styleName}


    and balcony = #{balcony}

ORDER BY house.floor_id

mapper.update:
update house
set state=#{state}
where house_id = #{houseId}
