mapper.select:
select a.repair_id, a.rent_id, a.user_id, a.message, a.report, a.fix, a.price, a.remark, a.state, c.house_name from repair as a, house as c, rent as d
d.rent_id = a.rent_id and c.house_id = d.house_id
and c.houseName = #{houseName}
and DATE_FORMAT(a.report, '%Y-%m-%d') = #{report}
${ew.customSqlSegment}

select a.repair_id, a.rent_id, a.message, a.report, a.state, a.fix
from repair as a,
     rent as b
where a.rent_id = b.rent_id
  and b.user_id = #{userId}
