mapper.update:
update report
set state = #{state}
where report_id = #{reportId}

update report
set views = views + 1
where report_id = #{reportId}

mapper.select:
select a.*, b.name, b.avatar
from report as a,
user as b
where a.user_id = b.user_id
and a.state = #{state}

    and a.report_id = #{reportId}


    and b.name = #{name}


    and a.report_id = #{reportId}

order by time DESC ${ew.customSqlSegment}
