mapper.update:
update role
set state = ${state}
where role_id = #{roleId}

update role
set role_name = #{roleName},
    remark    =#{remark}
where role_id = #{roleId}

mapper.select:
select a.*
from salary as a,
     salary_role as b
where b.role_id = #{roleId}
  and a.salary_id = b.salary_id
  and b.state not in (0, 2)
