mapper.update:
update salary set salary_name =#{salaryName}, remark = #{remark}, operation = #{operation}
,old_price = #{oldPrice}, price = #{price}, time = current_timestamp
where salary_id = #{salaryId}

mapper.select:
select count(*)
from salary_role
where salary_id = #{salaryId}

mapper.update:
update salary
set state = 0
where salary_id = #{salaryId}
